// ========================================
// VALIDACAO DE PARAMETROS DA REQUISICAO
// ========================================

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // No n8n, o body do POST vem em data.body
  let body = data.body;

  // Se body for string, fazer parse
  if (typeof body === 'string') {
    try {
      body = JSON.parse(body);
    } catch (e) {
      throw new Error('VALIDATION_ERROR: Body invalido - nao e JSON valido');
    }
  }

  // Se body nao existir, usar data diretamente (fallback)
  if (!body || typeof body !== 'object') {
    body = data;
  }

  // Extrair message
  const message = body.message || body.msg || body.text || '';

  // Validar campo obrigatorio: message
  if (!message || typeof message !== 'string' || message.trim() === '') {
    throw new Error('VALIDATION_ERROR: Campo "message" e obrigatorio e deve ser uma string nao vazia.');
  }

  // Extrair userId
  const userIdRaw = body.userId || body.user_id || '';

  // Validar userId (obrigatorio para sistema de assinatura)
  if (!userIdRaw || typeof userIdRaw !== 'string' || userIdRaw.trim() === '') {
    throw new Error('VALIDATION_ERROR: Campo "userId" e obrigatorio para verificacao de assinatura.');
  }

  // Extrair email (enviado pelo frontend)
  const email = body.email || '';

  // Extrair sessionId
  const sessionId = body.sessionId || body.session_id || `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  // Limitar tamanho da mensagem
  const userMessage = message.trim().substring(0, 2000);

  results.push({
    json: {
      userMessage,
      sessionId,
      userId: userIdRaw.trim(),
      email: email.trim().toLowerCase(),
      timestamp: new Date().toISOString(),
      requestId: `req_${Date.now()}_${Math.random().toString(36).substr(2, 6)}`
    }
  });
}

return results;

// ========================================
// VERIFICAR ASSINATURA DO USUARIO
// ========================================

// Lista de emails com acesso de admin (ilimitado)
const ADMIN_EMAILS = [
  'henriquer01@rojasdev.cloud',
  'admin@rojasdev.cloud'
];

// Dados da validacao anterior
const inputData = $('Validar Parametros').first().json;
const { userId, userMessage, sessionId, email, timestamp, requestId } = inputData;

// Resultado da busca no banco
const dbResults = $input.all();
const subscription = dbResults.length > 0 && dbResults[0].json.user_id ? dbResults[0].json : null;

// Verificar se e admin (pelo email enviado na requisicao)
const isAdminByRequestEmail = email && ADMIN_EMAILS.some(adminEmail => 
  email.toLowerCase() === adminEmail.toLowerCase()
);
const isAdminBySubscriptionEmail = subscription?.email && ADMIN_EMAILS.some(adminEmail => 
  subscription.email.toLowerCase() === adminEmail.toLowerCase()
);
const isAdminByMetadata = subscription?.metadata?.role === 'admin';
const isAdmin = isAdminByRequestEmail || isAdminBySubscriptionEmail || isAdminByMetadata;

// Verificar se usuario tem assinatura ativa
const hasActiveSubscription = subscription && 
  (subscription.status === 'active' || subscription.status === 'authorized');

// Admin sempre tem acesso
const accessGranted = isAdmin || hasActiveSubscription;

return [{
  json: {
    userId,
    userMessage,
    sessionId,
    email,
    timestamp,
    requestId,
    subscription: {
      hasActive: hasActiveSubscription || isAdmin,
      status: isAdmin ? 'admin' : (subscription?.status || 'none'),
      planId: subscription?.plan_id,
      planName: isAdmin ? 'Admin Ilimitado' : subscription?.plan_name,
      expiresAt: subscription?.next_payment_date,
      isAdmin: isAdmin
    },
    access: {
      granted: accessGranted,
      reason: isAdmin ? 'admin' : hasActiveSubscription ? 'active_subscription' : 'no_subscription'
    }
  }
}];

// ========================================
// EXTRAIR TERMOS DE BUSCA DA PERGUNTA
// ========================================

const items = $input.all();
const results = [];

// Palavras comuns a ignorar na busca
const stopWords = new Set([
  'o', 'a', 'os', 'as', 'um', 'uma', 'uns', 'umas',
  'de', 'da', 'do', 'das', 'dos', 'em', 'na', 'no', 'nas', 'nos',
  'por', 'para', 'com', 'sem', 'sob', 'sobre',
  'e', 'ou', 'mas', 'porem', 'contudo', 'entretanto',
  'que', 'qual', 'quais', 'quem', 'onde', 'quando', 'como', 'porque',
  'esse', 'essa', 'esses', 'essas', 'este', 'esta', 'estes', 'estas',
  'isso', 'isto', 'aquilo', 'aquele', 'aquela',
  'meu', 'minha', 'seu', 'sua', 'nosso', 'nossa',
  'ser', 'estar', 'ter', 'haver', 'fazer', 'ir', 'vir',
  'foi', 'era', 'seria', 'sido', 'sendo',
  'me', 'te', 'se', 'nos', 'vos', 'lhe', 'lhes',
  'muito', 'pouco', 'mais', 'menos', 'bem', 'mal',
  'sim', 'nao', 'talvez', 'ja', 'ainda', 'sempre', 'nunca',
  'aqui', 'ali', 'la', 'ca', 'onde',
  'biblia', 'versiculo', 'capitulo', 'livro', 'texto', 'diz', 'fala', 'disse'
]);

for (const item of items) {
  const { userMessage, sessionId, userId, timestamp, requestId, subscription, access } = item.json;

  // Extrair termos relevantes da pergunta
  const words = userMessage
    .toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '') // Remove acentos para busca
    .replace(/[^a-z0-9\s]/g, ' ') // Remove pontuacao
    .split(/\s+/)
    .filter(word => word.length > 2 && !stopWords.has(word));

  // Detectar referencias biblicas (ex: Joao 3:16, Genesis 1)
  const refPattern = /([1-3]?\s*[a-zA-Z]+)\s*(\d+)(?:[:.,](\d+))?(?:\s*[-a]\s*(\d+))?/gi;
  const references = [];
  let match;
  while ((match = refPattern.exec(userMessage)) !== null) {
    references.push({
      livro: match[1].trim(),
      capitulo: parseInt(match[2]),
      versiculoInicio: match[3] ? parseInt(match[3]) : null,
      versiculoFim: match[4] ? parseInt(match[4]) : null
    });
  }

  // Construir termos de busca (maximo 5 termos mais relevantes)
  const searchTerms = [...new Set(words)].slice(0, 5).join(' ');

  results.push({
    json: {
      userMessage,
      sessionId,
      userId,
      timestamp,
      requestId,
      subscription,
      access,
      searchTerms,
      references,
      hasReferences: references.length > 0
    }
  });
}

return results;

// ========================================
// COMBINAR RESULTADOS E CONSTRUIR CONTEXTO
// ========================================

// Pegar dados da extracao de termos
const inputData = $('Extrair Termos de Busca').first().json;

// Pegar resultados das buscas
const textSearchResults = $('Buscar na Biblia (PostgreSQL)').all().map(i => i.json);
const refSearchResults = $('Buscar por Referencia').all().map(i => i.json);

// Combinar resultados, priorizando referencias especificas
const allResults = [];
const seenRefs = new Set();

// Primeiro adiciona resultados de referencia especifica
for (const result of refSearchResults) {
  if (result.referencia && !seenRefs.has(result.referencia)) {
    seenRefs.add(result.referencia);
    allResults.push(result);
  }
}

// Depois adiciona resultados da busca por texto
for (const result of textSearchResults) {
  if (result.referencia && !seenRefs.has(result.referencia)) {
    seenRefs.add(result.referencia);
    allResults.push(result);
  }
}

// Limitar a 10 versiculos para o contexto
const contextVerses = allResults.slice(0, 10);

// Construir contexto biblico formatado
let bibleContext = '';
if (contextVerses.length > 0) {
  bibleContext = '\n\n## VERSICULOS RELEVANTES DA BIBLIA (use como referencia):\n';
  for (const verse of contextVerses) {
    bibleContext += `\n**${verse.referencia}**: "${verse.texto}"\n`;
  }
  bibleContext += '\n---\n';
}

return [{
  json: {
    ...inputData,
    bibleContext,
    versesFound: contextVerses.length,
    contextVerses
  }
}];

// ========================================
// CONSTRUCAO DO PROMPT COM CONTEXTO BIBLICO
// ========================================

const items = $input.all();
const results = [];

// Prompt de sistema com contexto biblico profundo
const BIBLE_SYSTEM_PROMPT = `Voce e um assistente espiritual cristao especializado na Biblia Sagrada. Seu papel e:

## IDENTIDADE E PROPOSITO
- Voce e um conselheiro biblico sabio, compassivo e acolhedor
- Seu conhecimento abrange toda a Biblia (Antigo e Novo Testamento)
- Voce conhece profundamente teologia crista, historia biblica e interpretacao das Escrituras
- IMPORTANTE: Voce tem acesso a uma base de dados com todos os versiculos da Biblia em portugues

## DIRETRIZES DE RESPOSTA
1. **Base Biblica**: Sempre fundamente suas respostas nas Escrituras Sagradas
2. **Citacoes**: Use os versiculos fornecidos no contexto e cite com referencia completa (ex: Joao 3:16)
3. **Contextualizacao**: Explique o contexto historico e cultural quando relevante
4. **Aplicacao Pratica**: Conecte os ensinamentos biblicos com a vida cotidiana
5. **Tom Acolhedor**: Seja gentil, empatico e encorajador
6. **Precisao**: Se nao souber algo com certeza, admita humildemente

## USANDO OS VERSICULOS DO CONTEXTO
- Quando versiculos relevantes forem fornecidos, USE-OS na sua resposta
- Cite o texto exato entre aspas seguido da referencia
- Se o usuario perguntar por uma referencia especifica, cite o versiculo completo
- Relacione os versiculos com a pergunta do usuario

## LIMITACOES
- Nao faca previsoes sobre o futuro ou profecias pessoais
- Nao substitua aconselhamento profissional (medico, juridico, psicologico)
- Nao entre em debates denominacionais divisivos
- Mantenha respeito por diferentes tradicoes cristas

## FORMATO DE RESPOSTA
- Seja conciso mas completo
- Use linguagem clara e acessivel
- Organize respostas longas em topicos quando apropriado
- Sempre termine com uma palavra de encorajamento ou reflexao

Responda em portugues brasileiro de forma natural e acolhedora.`;

for (const item of items) {
  const { userMessage, sessionId, userId, timestamp, requestId, subscription, access, bibleContext, versesFound } = item.json;

  // Construir mensagem do usuario com contexto biblico
  let enhancedUserMessage = userMessage;
  if (bibleContext && versesFound > 0) {
    enhancedUserMessage = userMessage + bibleContext;
  }

  // Construir mensagens para a API
  const messages = [
    {
      role: 'system',
      content: BIBLE_SYSTEM_PROMPT
    },
    {
      role: 'user',
      content: enhancedUserMessage
    }
  ];

  results.push({
    json: {
      messages,
      userMessage,
      sessionId,
      userId,
      timestamp,
      requestId,
      subscription,
      access,
      versesFound,
      model: 'gpt-4o-mini',
      maxTokens: 1500,
      temperature: 0.7
    }
  });
}

return results;

// ========================================
// PROCESSAR RESPOSTA DA IA
// ========================================

const items = $input.all();
const inputData = $('Construir Prompt Biblico').first().json;
const results = [];

for (const item of items) {
  const response = item.json;

  if (!response.body || !response.body.choices || !response.body.choices[0]) {
    throw new Error('AI_ERROR: Resposta invalida da API de IA');
  }

  const aiResponse = response.body.choices[0].message.content;
  const usage = response.body.usage || {};

  results.push({
    json: {
      response: aiResponse,
      status: 'success',
      metadata: {
        requestId: inputData.requestId,
        sessionId: inputData.sessionId,
        userId: inputData.userId,
        timestamp: inputData.timestamp,
        responseTimestamp: new Date().toISOString(),
        model: response.body.model || inputData.model,
        versesUsed: inputData.versesFound || 0,
        tokensUsed: {
          prompt: usage.prompt_tokens || 0,
          completion: usage.completion_tokens || 0,
          total: usage.total_tokens || 0
        },
        subscription: {
          status: inputData.subscription.status,
          accessReason: inputData.access.reason
        }
      }
    }
  });
}

return results;

// ========================================
// RESPOSTA PARA USUARIO SEM ASSINATURA
// ========================================

const items = $input.all();
const results = [];

for (const item of items) {
  const { userId, requestId, timestamp, subscription, access } = item.json;

  // Mensagem personalizada baseada no status
  let message = '';
  let subscriptionLink = 'https://seu-site.com/assinar'; // Altere para seu link

  if (subscription.status === 'cancelled') {
    message = 'Sua assinatura foi cancelada. Reative para continuar usando o Bible AI Chat.';
  } else if (subscription.status === 'paused') {
    message = 'Sua assinatura esta pausada. Reative-a para continuar conversando.';
  } else if (subscription.status === 'pending') {
    message = 'Seu pagamento esta pendente. Conclua o pagamento para acessar o chat.';
  } else {
    message = 'Para usar o Bible AI Chat, voce precisa de uma assinatura ativa. Assine agora e tenha acesso ilimitado as conversas sobre a Biblia!';
  }

  results.push({
    json: {
      response: null,
      status: 'subscription_required',
      error: {
        type: 'SUBSCRIPTION_REQUIRED',
        message,
        code: 402
      },
      subscription: {
        required: true,
        currentStatus: subscription.status,
        subscriptionLink,
        plans: [
          { id: 'monthly', name: 'Mensal', price: 'R$ 19,90/mes' },
          { id: 'quarterly', name: 'Trimestral', price: 'R$ 49,90/trimestre' },
          { id: 'yearly', name: 'Anual', price: 'R$ 149,90/ano' }
        ]
      },
      metadata: {
        requestId,
        userId,
        timestamp
      }
    }
  });
}

return results;

// ========================================
// TRATAMENTO DE ERROS CENTRALIZADO
// ========================================

const error = $input.first().json;
const errorMessage = error.message || error.error?.message || 'Erro desconhecido';
const errorStack = error.stack || '';

let errorType = 'INTERNAL_ERROR';
let statusCode = 500;
let userMessage = 'Ocorreu um erro interno. Por favor, tente novamente.';

if (errorMessage.includes('VALIDATION_ERROR')) {
  errorType = 'VALIDATION_ERROR';
  statusCode = 400;
  userMessage = errorMessage.replace('VALIDATION_ERROR: ', '');
} else if (errorMessage.includes('AI_ERROR')) {
  errorType = 'AI_SERVICE_ERROR';
  statusCode = 502;
  userMessage = 'O servico de IA esta temporariamente indisponivel. Tente novamente em alguns instantes.';
} else if (errorMessage.includes('timeout') || errorMessage.includes('ETIMEDOUT')) {
  errorType = 'TIMEOUT_ERROR';
  statusCode = 504;
  userMessage = 'A requisicao demorou muito para ser processada. Tente novamente.';
} else if (errorMessage.includes('401') || errorMessage.includes('Unauthorized')) {
  errorType = 'AUTH_ERROR';
  statusCode = 500;
  userMessage = 'Erro de configuracao do servico. Contate o administrador.';
} else if (errorMessage.includes('429') || errorMessage.includes('rate limit')) {
  errorType = 'RATE_LIMIT_ERROR';
  statusCode = 429;
  userMessage = 'Muitas requisicoes. Aguarde alguns segundos e tente novamente.';
} else if (errorMessage.includes('relation') || errorMessage.includes('does not exist')) {
  errorType = 'DATABASE_ERROR';
  statusCode = 500;
  userMessage = 'Base de dados da Biblia nao encontrada. Execute o script de importacao.';
}

return [{
  json: {
    response: null,
    status: 'error',
    error: {
      type: errorType,
      message: userMessage,
      code: statusCode,
      timestamp: new Date().toISOString()
    }
  },
  statusCode
}];
